(293, 1, '2024-02-13 19:33:20', '2024-02-13 18:33:20', '<!-- wp:paragraph -->\n<p><strong><em>Prezzo </em></strong>:   3,50 euro cada l\'una</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"sizeSlug\":\"large\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"https://www.palermoviva.it/wordpress/wp-content/uploads/2017/10/arancina-al-cioccolato.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3 class=\"wp-block-heading\">Ingredienti per le arancine al cioccolato:</h3>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">Ripieno:</h4>\n<!-- /wp:heading -->\n\n<!-- wp:list {\"ordered\":true} -->\n<ol><!-- wp:list-item -->\n<li>200 g di cioccolato fondente, tritato</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>1/2 tazza di panna fresca</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>2 cucchiai di zucchero a velo (o piÃ¹, a seconda del tuo gusto)</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Scorza d\'arancia grattugiata (opzionale)</li>\n<!-- /wp:list-item --></ol>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">Riso:</h4>\n<!-- /wp:heading -->\n\n<!-- wp:list {\"ordered\":true} -->\n<ol><!-- wp:list-item -->\n<li>400 g di riso per dolci o riso tipo Carnaroli</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>1,2 litri di latte</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Zucchero a piacere</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>50 g di burro</li>\n<!-- /wp:list-item --></ol>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">Panatura:</h4>\n<!-- /wp:heading -->\n\n<!-- wp:list {\"ordered\":true} -->\n<ol><!-- wp:list-item -->\n<li>Uova sbattute</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Pangrattato o farina di cocco (a scelta)</li>\n<!-- /wp:list-item --></ol>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":3} -->